var validaNumeros = require("../../baseGenerica/validaNumeros.js");
var formatSegundos = require("../../baseGenerica/formatarSegundos.js");
var dataFormatSoma = require("../../baseGenerica/dataFormatSoma");

import Vue from "vue";

import KLoading from "../../components/KLoading.vue";
import KDialog from "../../components/KDialog.vue";
import KDataList from "../../components/KDataList.vue";

import { Display } from "../../configuration/display";

import { Header, HeaderIcon, HeaderAction } from "../../models/header";
import { Headers } from "../../models/headers";
import moment from "moment";

import {
  ITurnoListaFormView,
  ITurnoListaFormController,
} from "../../contracts/Cadastro/TurnoListaFormContract";

import { TurnoListaFormController } from "../../controllers/Cadastro/TurnoListaFormController";

import Component from "vue-class-component";

import { Validation, ValidationSchema } from "../../baseGenerica/validation";

@Component({
  components: {
    KLoading,
    KDialog,
    KDataList,
  },
})
export default class TurnoListaForm extends Vue implements ITurnoListaFormView {
  _controller?: ITurnoListaFormController;

  dialogLoading = false;
  dialogMessage = false;
  textError = "";

  search = "";

  dialogDelete = false;
  dialogAtivar = false;
  itemFuncionarioDelete: any;

  dialogAddTurno = false;
  idTurno = "";

  //SNACKBAR
  snackbar = false;
  text = "";
  vertical = true;
  color = "";
  colorBorder = "";

  caminho = [
    {
      text: "Home",
      disabled: false,
      href: "/",
    },
    {
      text: "Turnos",
      disabled: false,
      href: "/turnos",
    },
  ];

  horarioImprodutivo: any = {
    horarioInicial: null,
    horaFinal: null,
    descricao: "",
    total: null,
  };
  itensListaHorarioImprodutivo: any = [];

  totalSegundosDias = 86400;

  turno = {
    codigo: "",
    descricao: "",
    sabado: false,
    domingo: false,
    horaInicial: "",
    horaFinal: "",
    horaTotal: "",
    horaTotalInprodutiva: "",
    observacao: "",
    dataCadastro: new Date(),
    deletado: "N",
    turnoFinalDiaSeguinte: false,
    horariosImprodutivos: this.itensListaHorarioImprodutivo,
  };

  limparCampos(): void {
    this.turno = {
      codigo: "",
      descricao: "",
      sabado: false,
      domingo: false,
      horaInicial: "",
      horaFinal: "",
      horaTotal: "",
      horaTotalInprodutiva: "",
      observacao: "",
      dataCadastro: new Date(),
      deletado: "N",
      turnoFinalDiaSeguinte: false,
      horariosImprodutivos: [],
    };

    this.itensListaHorarioImprodutivo = [];
  }

  isMobile = false;

  removeItemHorarioImprodutivo(itemSelecionado: any) {
    for (const { item, index } of this.itensListaHorarioImprodutivo.map(
      (item: any, index: any) => ({
        item,
        index,
      })
    )) {
      if (
        item.descricao == itemSelecionado.descricao &&
        item.horaInicial == itemSelecionado.horaInicial &&
        item.horaFinal == itemSelecionado.horaFinal
      ) {
        this.itensListaHorarioImprodutivo.splice(index, 1);
      }
    }

    this.somaHoraTotalItensImprodutivo();
    this.somaHoraTotal();
  }

  headersItensHorarioImprodutivo = [
    {
      text: "Descrição",
      align: "center",
      filterable: true,
      value: "descricao",
    },
    {
      text: "Horário Inicial",
      align: "center",
      filterable: true,
      value: "horaInicial",
    },
    {
      text: "Horário Final",
      align: "center",
      filterable: true,
      value: "horaFinal",
    },
    {
      text: "Total Intervalo",
      align: "center",
      filterable: true,
      value: "horaTotal",
    },

    //{ text: "Ação", align: "center", filterable: true, value: "acoes" },
  ];

  headers = new Headers([
    new Header("Status", "deletado", false, false, "center", false, "100"),
    new Header("Código", "codigo", false, false, "left", true, "200"),
    new Header("Descrição", "descricao", false, false, "left", true, "200"),
    new Header(
      "Hr. Inicial",
      "fHoraInicial",
      false,
      false,
      "left",
      false,
      "200"
    ),
    new Header("Hr. Final", "fHoraFinal", false, false, "left", false, "200"),
    new Header("Hora Total", "horaTotal", false, false, "left", false, "200"),
    new Header("Ações", "acoes", true, false, "center", false, "80"),
  ]);

  itens: any = [];

  imputChangesTimes() {
    this.somaHoraTotalItensImprodutivo();
    this.somaHoraTotal();
  }

  addHorarioImprodutivo() {
    if (!this.validateHorarioInprodutivo()) {
      this.itensListaHorarioImprodutivo.push(
        JSON.parse(JSON.stringify(this.horarioImprodutivo))
      );
      this.horarioImprodutivo = {};
      this.somaHoraTotalItensImprodutivo();
      this.somaHoraTotal();
    }
  }

  cancelaSaveTurno() {
    this.limparCampos();
    this.dialogAddTurno = false;
  }

  formatarSegundos(h: any, min: any, s: any) {
    return formatSegundos(h, min, s);
  }

  dataFormatarSoma(s: any) {
    return dataFormatSoma(s);
  }

  somaHoraTotalHorarioImprodutivo() {
    if (!this.validateTimesCamposPreenchidosHoraioImprodutivo()) {
      let hInicial = moment(
        this.horarioImprodutivo.horaInicial,
        "HH:mm:ss"
      ).toDate();
      let hFinal = moment(
        this.horarioImprodutivo.horaFinal,
        "HH:mm:ss"
      ).toDate();

      //convertendo em segundos
      let hInicialConvert = this.formatarSegundos(
        hInicial.getHours(),
        hInicial.getMinutes(),
        hInicial.getSeconds()
      );

      let hFinalConvert = this.formatarSegundos(
        hFinal.getHours(),
        hFinal.getMinutes(),
        hFinal.getSeconds()
      );

      //calculo de regra de negocio
      let tHoraFinal = hFinalConvert - hInicialConvert;

      this.horarioImprodutivo.horaTotal = this.dataFormatarSoma(tHoraFinal);
    }
  }

  somaHoraTotalItensImprodutivo() {
    let totalTime = 0;
    this.itensListaHorarioImprodutivo.forEach((element: any) => {
      let hInicial = moment(element.horaInicial, "HH:mm:ss").toDate();
      let hFinal = moment(element.horaFinal, "HH:mm:ss").toDate();

      //convertendo em segundos
      let hInicialConvert = this.formatarSegundos(
        hInicial.getHours(),
        hInicial.getMinutes(),
        hInicial.getSeconds()
      );
      let hFinalConvert = this.formatarSegundos(
        hFinal.getHours(),
        hFinal.getMinutes(),
        hFinal.getSeconds()
      );

      //calculo de regra de negocio
      let tHoraFinal = hFinalConvert - hInicialConvert;
      totalTime += tHoraFinal; //- (tAlmoço + tCafe + tPausa);
    });

    this.turno.horaTotalInprodutiva = this.dataFormatarSoma(totalTime);
  }

  somaHoraTotal() {
    if (!this.validateTimesCamposPreenchidos()) {
      //convertendo times de string para dateTime
      let hInicial = moment(this.turno.horaInicial, "HH:mm:ss").toDate();
      let hFinal = moment(this.turno.horaFinal, "HH:mm:ss").toDate();

      //convertendo em segundos
      let hInicialConvert = this.formatarSegundos(
        hInicial.getHours(),
        hInicial.getMinutes(),
        hInicial.getSeconds()
      );
      let hFinalConvert = this.formatarSegundos(
        hFinal.getHours(),
        hFinal.getMinutes(),
        hFinal.getSeconds()
      );

      //horario improdutivo
      let horariosImprodutivosConvert = 0;
      if (this.itensListaHorarioImprodutivo.length > 0) {
        let horariosImprodutivos = moment(
          this.turno.horaTotalInprodutiva,
          "HH:mm:ss"
        ).toDate();
        //convertendo horario improdutivo em segundos
        horariosImprodutivosConvert = this.formatarSegundos(
          horariosImprodutivos.getHours(),
          horariosImprodutivos.getMinutes(),
          horariosImprodutivos.getSeconds()
        );
      }

      //calculo de regra de negocio
      let tHoraFinal = 0;
      if (hFinalConvert < hInicialConvert) {
        let timeFinalDiaSeguinte = 0;
        timeFinalDiaSeguinte = this.totalSegundosDias - hInicialConvert;
        this.turno.turnoFinalDiaSeguinte = true;
        tHoraFinal = timeFinalDiaSeguinte + hFinalConvert;
      } else {
        this.turno.turnoFinalDiaSeguinte = false;
        tHoraFinal = hFinalConvert - hInicialConvert;
      }

      let totalTime = tHoraFinal - horariosImprodutivosConvert; //- (tAlmoço + tCafe + tPausa);

      this.turno.horaTotal = this.dataFormatarSoma(totalTime);
    }
  }

  validateHorarioInprodutivo() {
    if (
      this.horarioImprodutivo.descricao == "" ||
      this.horarioImprodutivo.descricao == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Informe a descrição para o Horário Improdutivo.";
      this.snackbar = true;
      return true;
    }

    if (
      this.horarioImprodutivo.horaInicial == "" ||
      this.horarioImprodutivo.horaInicial == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O Horário Inicial com timer incorreto ou invalido.";
      this.snackbar = true;
      return true;
    }

    if (
      this.horarioImprodutivo.horaFinal == "" ||
      this.horarioImprodutivo.horaFinal == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O Horário Final com timer incorreto ou invalido.";
      this.snackbar = true;
      return true;
    }

    if (
      this.horarioImprodutivo.horaFinal < this.horarioImprodutivo.horaInicial
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O Horário Final não pode ser superior a Horário Inicial.";
      this.snackbar = true;
      return true;
    }

    //tratando segunbdos
    if (this.horarioImprodutivo.horaInicial.length == 5) {
      this.horarioImprodutivo.horaInicial =
        this.horarioImprodutivo.horaInicial + ":00";
    }

    if (this.horarioImprodutivo.horaFinal.length == 5) {
      this.horarioImprodutivo.horaFinal =
        this.horarioImprodutivo.horaFinal + ":00";
    }

    return false;
  }

  validate() {
    let schema: ValidationSchema[] = [];

    //console.log(this.turno.horaInicial);

    schema.push(new ValidationSchema("codigo", "Código", "string", true, 10));
    schema.push(
      new ValidationSchema("descricao", "Descrição", "string", true, 50)
    );

    let result = new Validation().ValidSchema(schema, this.turno);
    if (result.sucesso == false) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = result.mensagem;
      this.snackbar = true;
      return true;
    }

    if (this.turno.horaInicial == "" || this.turno.horaInicial == null) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O Hora Inicial com timer incorreto ou invalido.";
      this.snackbar = true;
      return true;
    }

    if (this.turno.horaFinal == "" || this.turno.horaFinal == null) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O Hora Final com timer incorreto ou invalido.";
      this.snackbar = true;
      return true;
    }

    if (this.turno.horaFinal == "" || this.turno.horaFinal == null) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O Hora Final com timer incorreto ou invalido.";
      this.snackbar = true;
      return true;
    }

    // if (
    //   this.validateTimesDados(
    //     this.turno.horaInicial,
    //     this.turno.horaFinal,
    //     "A Hora Inicial não pode ser superior a Hora final e a Hora Final não pode ser inferior a Hora Inicial."
    //   )
    // ) {
    //   return true;
    // }
    return false;
  }

  validateTimesCamposPreenchidosHoraioImprodutivo() {
    if (
      this.horarioImprodutivo.horaInicial == "" ||
      this.horarioImprodutivo.horaFinal == null
    ) {
      return true;
    }

    if (
      this.horarioImprodutivo.horaInicial == "" ||
      this.horarioImprodutivo.horaFinal == null
    ) {
      return true;
    }

    return false;
  }

  validateTimesCamposPreenchidos() {
    if (this.turno.horaInicial == "" || this.turno.horaInicial == null) {
      return true;
    }

    if (this.turno.horaFinal == "" || this.turno.horaFinal == null) {
      return true;
    }

    if (this.turno.horaFinal == "" || this.turno.horaFinal == null) {
      return true;
    }

    return false;
  }

  // validateTimesDados(hInicial: any, hFinal: any, mensagem: string) {
  //   let inicial = moment(hInicial, "HH:mm:ss").toDate();
  //   let final = moment(hFinal, "HH:mm:ss").toDate();

  //   if (final < inicial) {
  //     this.colorBorder = "error";
  //     this.color = "error";
  //     this.text = mensagem;
  //     this.snackbar = true;
  //     return;
  //   }

  //   if (inicial > final) {
  //     this.colorBorder = "error";
  //     this.color = "error";
  //     this.text = mensagem;
  //     this.snackbar = true;
  //     return;
  //   }

  //   return false;
  // }

  updateTurno() {
    this.turno.horariosImprodutivos = this.itensListaHorarioImprodutivo;
    //console.log(this.turno);

    if (this.validate()) {
    } else {
      //this.solicitacao.user = parseInt(Storage.get("User")!);
      this._controller!.update(this.idTurno, this.turno);
      this.idTurno = "";
      this.dialogAddTurno = false;
    }
  }

  saveTurno() {
    if (this.validate()) {
    } else {
      this.turno.horariosImprodutivos = this.itensListaHorarioImprodutivo;
      //this.solicitacao.user = parseInt(Storage.get("User")!);
      this._controller!.salvar(this.turno);
    }
  }

  apenasNumeros(event: any) {
    validaNumeros(event);
  }

  loading(visible: boolean): void {
    this.dialogLoading = visible;
  }
  showMsg(msg: string): void {
    this.textError = msg;
    this.dialogMessage = true;
    this.dialogAddTurno = false;
  }
  showItens(list: any): void {
    this.itens = [];
    list.forEach((element: any) => {
      element.fHoraInicial = moment(element.horaInicial).format("HH:mm:ss");
      element.fHoraFinal = moment(element.horaFinal).format("HH:mm:ss");
      this.itens.push(element);
    });
  }
  fecharDialogMessage() {
    this.dialogMessage = false;
  }

  deleteItem(item: any) {
    this.itemFuncionarioDelete = item;

    if (item.deletado == "S") {
      this.dialogAtivar = true;
    }

    if (item.deletado == "N") {
      this.dialogDelete = true;
    }
  }

  deleteItemConfirmado() {
    this.dialogDelete = false;
    this._controller!.deleteitem(this.itemFuncionarioDelete.id);
    this.itemFuncionarioDelete = [];
  }

  ativarItemConfirmado() {
    this.dialogAtivar = false;
    this._controller!.ativaritem(this.itemFuncionarioDelete.id);
    this.itemFuncionarioDelete = [];
  }

  editarItem(item: any) {
    //console.log(item);
    this._controller!.carregarItemEditar(item.id);
    //this.$router.push("/funcionario/" + item.id + "/" + item.matricula);
  }

  addSolicitacao() {
    this.limparCampos();
    this.itensListaHorarioImprodutivo = [];

    this.dialogAddTurno = true;

    //this.$router.push("/perfil");
  }

  carregaDadosTurnoEditar(turno: any): void {
    this.turno = turno;

    turno.turnoHorariosImprodutivos.forEach((element: any) => {
      element.horaInicial = new Date(element.horaInicial)
        .toLocaleTimeString()
        .toString();
      element.horaFinal = new Date(element.horaFinal)
        .toLocaleTimeString()
        .toString();
    });

    this.itensListaHorarioImprodutivo = turno.turnoHorariosImprodutivos;

    this.turno.horaInicial = moment(turno.horaInicial).format("HH:mm");
    this.turno.horaFinal = moment(turno.horaFinal).format("HH:mm");

    this.idTurno = turno.id;

    this.imputChangesTimes();
    this.dialogAddTurno = true;
  }

  created() {
    this.isMobile = Display.isMobile();
    this._controller = new TurnoListaFormController(this);

    this._controller.carregarItens();
  }
}
