var validaNumeros = require("../../baseGenerica/validaNumeros.js");

import Vue from "vue";

import KLoading from "../../components/KLoading.vue";
import KDialog from "../../components/KDialog.vue";
import KDataList from "../../components/KDataList.vue";

import Component from "vue-class-component";
import { Watch } from "vue-property-decorator";

import { Display } from "../../configuration/display";
import { Storage } from "../../configuration/storage";

import { Header, HeaderIcon, HeaderAction } from "../../models/header";
import { Headers } from "../../models/headers";

import {
  ILinhaTransferenciaFormView,
  ILinhaTransferenciaFormController,
} from "../../contracts/Linha/LinhaTransferenciaFormContract";
import { LinhaTransferenciaFormController } from "../../controllers/Linha/LinhaTransferenciaFormController";

@Component({
  components: {
    KLoading,
    KDialog,
    KDataList,
  },
})
export default class LinhaTransferenciaForm
  extends Vue
  implements ILinhaTransferenciaFormView {
  _controller?: ILinhaTransferenciaFormController;

  caminho = [
    {
      text: "Home",
      disabled: false,
      href: "/",
    },
    {
      text: "RotaAqui",
      disabled: false,
      href: "/rota",
    },
  ];

  //validate form
  valid = true;
  lazy = false;

  isMobile = false;

  isSucesso = false;

  itemsLinha: any = [];
  linhaSelecionada: any = [];
  
  itemsLinhaOrdemProducaoPlano: any = [];
  linhaOrdemProducaoPlanoSelecionada: any = [];
  idOrdemProducaoPlanoTransfererir = 0;

  itemsFase: any = [];
  faseSelecionada: any = [];

  itemsOperacao: any = [];
  operacaoSelecionada: any = [];

  itemsHabilidade: any = [];
  habilidadeSelecionada: any = [];

  itemsOperador: any = [];
  operadorSelecionada: any = [];

  // LINHAS A TRANSFERIR
  itemsLinhaTransferir: any = [];
  linhaSelecionadaTransferir: any = [];

  itemsFaseTransferir: any = [];
  faseSelecionadaTransferir: any = [];

  itemsOperacaoTransferir: any = [];
  operacaoSelecionadaTransferir: any = [];

  itemsHabilidadeTransferir: any = [];
  habilidadeSelecionadaTransferir: any = [];

  disableFase = true;
  disableOperacao = true;
  disableHabilidade = true;
  disableOperador = true;

  disableFaseTransferir = true;
  disableOperacaoTransferir = true;
  disableHabilidadeTransferir = true;

  dialogLoading = false;
  dialogMessage = false;
  textError = "";

  //SNACKBAR
  snackbar = false;
  text = "";
  vertical = true;
  color = "";
  colorBorder = "";

  apenasNumeros(event: any) {
    validaNumeros(event);
  }

  loading(visible: boolean): void {
    this.dialogLoading = visible;
  }

  fecharDialogMessage() {
    this.dialogMessage = false;
    if (this.isSucesso) {
      this.$router.push("/linhascontrole");
    }
  }

  retornaListaLinhaControle() {
    this.$router.push("/linhascontrole");
  }

  showMsg(msg: string, modo: number): void {
    this.textError = msg;
    if (modo == 1) {
      this.isSucesso = true;
    }
    this.dialogMessage = true;
  }

  validate() {

    if (
      this.linhaSelecionada == "" ||
      this.linhaSelecionada == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a linha atual.";
      this.snackbar = true;
      return true;
    }


if (
      this.idOrdemProducaoPlanoTransfererir == 0 ||
      this.idOrdemProducaoPlanoTransfererir == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione o Produto / Nº O.P. para onde deseja realizar a transferência do operador.";
      this.snackbar = true;
      return true;
    }
    
     if (
      this.faseSelecionada == "" ||
      this.faseSelecionada == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a fase atual.";
      this.snackbar = true;
      return true;
    }

     if (
      this.operacaoSelecionada == "" ||
      this.operacaoSelecionada == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a operação atual.";
      this.snackbar = true;
      return true;
    }

     if (
      this.habilidadeSelecionada == "" ||
      this.habilidadeSelecionada == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a habilidade atual.";
      this.snackbar = true;
      return true;
    }

     if (
      this.operadorSelecionada == "" ||
      this.operadorSelecionada == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione o operador para solicitar a transferência.";
      this.snackbar = true;
      return true;
    }

     if (
      this.linhaSelecionadaTransferir == "" ||
      this.linhaSelecionadaTransferir == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a linha a transferir.";
      this.snackbar = true;
      return true;
    }

     if (
      this.faseSelecionadaTransferir == "" ||
      this.faseSelecionadaTransferir == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a fase a transferir.";
      this.snackbar = true;
      return true;
    }

     if (
      this.operacaoSelecionadaTransferir == "" ||
      this.operacaoSelecionadaTransferir == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a operação a transferir.";
      this.snackbar = true;
      return true;
    }

     if (
      this.linhaSelecionada.id == this.linhaSelecionadaTransferir.id && (
      this.habilidadeSelecionada.id == this.habilidadeSelecionadaTransferir.id ||
      this.habilidadeSelecionadaTransferir == "" ||
      this.habilidadeSelecionadaTransferir == null )
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a habilidade a transferir. A habilidade a transferir deve ser diferente da Habilidade atual.";
      this.snackbar = true;
      return true;
    }

    return false;
  }

  saveLinhaSolicitacaoTransferencia() {
    if (this.validate()) {
      
    } else {
      let dadosSolicitacaiTransferencia = {
        idLinha                         : this.linhaSelecionada.id,
        idLinhaCapacidade               : this.faseSelecionada.id,
        idOperacao                      : this.operacaoSelecionada.id,
        idHabilidade                    : this.habilidadeSelecionada.id,
        idOperador                      : this.operadorSelecionada.idFuncionario,
        idOrdemProducaoPlano            : this.operadorSelecionada.idOrdemProducaoPlano,

        idlinhaTransferir               : this.linhaSelecionadaTransferir.id,
        idLinhaCapacidadeTransferir     : this.faseSelecionadaTransferir.id,
        idOperacaoTransferir            : this.operacaoSelecionadaTransferir.id,
        idHabilidadeTransferir          : this.habilidadeSelecionadaTransferir.id,
        idOrdemProducaoPlanoTransferir  : this.idOrdemProducaoPlanoTransfererir,

        dataCadastro                    : new Date(),
        status                          : "AC",
      };

      this._controller!.salvar(dadosSolicitacaiTransferencia);
    }
  }

  updateFuncionario() {
    if (!this.validate()) {
    } else {
      //this.solicitacao.user = parseInt(Storage.get("User")!);
      // this._controller!.update(this.idFuncionario, this.funcionario);
      this.retornaListaLinhaControle();
    }
  }

  carregarDtos(): void {}

  showBaseForEditar(item: any) {
    //CARREGANDO DADOS DO FUNCIONARIO
  }

  //carregando todas as linhas
  carregaLinha(itemLinha: any): void {
    this.itemsLinha = itemLinha;
    this.itemsFase = [];
    this.itemsOperacao = [];
    this.itemsHabilidade = [];
    this.itemsOperador = [];
    this.disableFase = false;
    this.disableOperacao = true;
    this.disableHabilidade = true;
    this.disableOperador = true;
  }

  //carregando Ordem de Producao Plano para Transferncia de Linhas
  carregaLinhaOrdemProducaoPlano(itemLinhaOrdemProducaoPlano: any): void {
    this.itemsLinhaOrdemProducaoPlano = itemLinhaOrdemProducaoPlano;
  }

  changeLinhaOrdemProducaoPlano() {
    this.linhaOrdemProducaoPlanoSelecionada = [];
    this.itemsLinhaOrdemProducaoPlano.forEach((element: any) => {
        if (element.idLinha ==  this.linhaSelecionadaTransferir.id){
          this.linhaOrdemProducaoPlanoSelecionada.push(element);
        }
    });
  }

  //carregando a fase da linha selecionada
  changeFase() {
    this.itemsFase = [];
    this.itemsOperacao = [];
    this.itemsHabilidade = [];
    this.itemsOperador = [];
    this.disableOperacao = false;
    this.disableHabilidade = true;
    this.disableOperador = true;

    //carregando dados da fase (Capacidade da Linha)
    let linhaCapacidade: any = [];

    this.itemsLinha.forEach((element: any) => {
      if (
        this.linhaSelecionada.id == element.id &&
        this.linhaSelecionada.nome == element.nome
      ) {
        linhaCapacidade.push(element.linhaCapacidade);
      }
    });

    this.itemsFase = linhaCapacidade[0];
  }

  //carregando a Operacao da fase selecionada
  changeOperacao() {
    this.itemsOperacao = [];
    this.itemsHabilidade = [];
    this.itemsOperador = [];
    this.disableHabilidade = false;
    this.disableOperador = true;
    //carregando dados da operacao (Fase - Capacidade da Linha)
    let operacao: any = [];

    this.itemsLinha.forEach((elementLinha: any) => {
      elementLinha.linhaCapacidade.forEach((elementLinhaCapacidade: any) => {
        if (
          this.linhaSelecionada.id == elementLinha.id &&
          this.linhaSelecionada.nome == elementLinha.nome &&
          this.faseSelecionada.id == elementLinhaCapacidade.id &&
          this.faseSelecionada.fase == elementLinhaCapacidade.fase
        ) {
          operacao.push(elementLinhaCapacidade.processoOperacao);
        }
      });
    });

    this.itemsOperacao = operacao;
  }

  //carregando a Habilidades da Fase selecionada
  changeHabilidades() {
    this.itemsHabilidade = [];
    this.disableOperador = false;

    //carregando dados da habilidade (Fase - Capacidade da Linha)
    let habilidades: any = [];

    this.itemsLinha.forEach((elementLinha: any) => {
      elementLinha.linhaCapacidade.forEach((elementLinhaCapacidade: any) => {
        elementLinhaCapacidade.linhaCapacidadeHabilidades.forEach(
          (elementHabilidade: any) => {
            if (
              this.linhaSelecionada.id == elementLinha.id &&
              this.linhaSelecionada.nome == elementLinha.nome &&
              this.faseSelecionada.id == elementLinhaCapacidade.id &&
              this.faseSelecionada.fase == elementLinhaCapacidade.fase &&
              this.operacaoSelecionada.id ==
                elementLinhaCapacidade.processoOperacao.id &&
              this.operacaoSelecionada.nome ==
                elementLinhaCapacidade.processoOperacao.nome
            ) {
              habilidades.push(elementHabilidade.processoHabilidade);
            }
          }
        );
      });
    });

    this.itemsHabilidade = habilidades;
  }

  //carregando os operadores com a Habilidades da Fase selecionada
  changeOperadores() {
    this.itemsOperador = [];
    //carregando dados dos operadores da habilidade  selecionada(Fase - Capacidade da Linha)
    let operadores: any = [];

    this.itemsLinha.forEach((elementLinha: any) => {
      elementLinha.linhaCapacidade.forEach((elementLinhaCapacidade: any) => {
        elementLinhaCapacidade.linhaCapacidadeHabilidades.forEach(
          (elementHabilidade: any) => {
            elementHabilidade.operadores.forEach((elementOperadores: any) => {
              if (
                this.linhaSelecionada.id == elementLinha.id &&
                this.linhaSelecionada.nome == elementLinha.nome &&
                this.faseSelecionada.id == elementLinhaCapacidade.id &&
                this.faseSelecionada.fase == elementLinhaCapacidade.fase &&
                this.operacaoSelecionada.id ==
                  elementLinhaCapacidade.processoOperacao.id &&
                this.operacaoSelecionada.nome ==
                  elementLinhaCapacidade.processoOperacao.nome &&
                this.habilidadeSelecionada.id ==
                  elementHabilidade.processoHabilidade.id &&
                this.habilidadeSelecionada.nome ==
                  elementHabilidade.processoHabilidade.nome
              ) {
                operadores.push(elementOperadores);
              }
            });
          }
        );
      });
    });
    this.itemsOperador = operadores;
  }

  //carregando todas as linhas a transferir
  carregaLinhaTransferir(itemLinha: any): void {
    this.itemsLinhaTransferir = itemLinha;
    this.itemsFaseTransferir = [];
    this.itemsOperacaoTransferir = [];
    this.itemsHabilidadeTransferir = [];
    // this.itemsOperador = [];
    this.disableFaseTransferir = false;
    this.disableOperacaoTransferir = true;
    this.disableHabilidadeTransferir = true;
    // this.disableOperador = true;
  }

  //carregando a fase da linha selecionada a transferir
  changeFaseTransferir() {
    this.changeLinhaOrdemProducaoPlano();

    this.itemsFaseTransferir = [];
    this.itemsOperacaoTransferir = [];
    this.itemsHabilidadeTransferir = [];
    // this.itemsOperador = [];
    this.disableOperacaoTransferir = false;
    this.disableHabilidadeTransferir = true;
    // this.disableOperador = true;

    //carregando dados da fase (Capacidade da Linha)
    let linhaCapacidade: any = [];

    this.itemsLinhaTransferir.forEach((element: any) => {
      if (
        this.linhaSelecionadaTransferir.id == element.id &&
        this.linhaSelecionadaTransferir.nome == element.nome
      ) {
        linhaCapacidade.push(element.linhaCapacidade);
      }
    });

    this.itemsFaseTransferir = linhaCapacidade[0];

  }

  //carregando a Operacao da fase selecionada Transferir
  changeOperacaoTransferir() {
    this.itemsOperacaoTransferir = [];
    this.itemsHabilidadeTransferir = [];
    // this.itemsOperador = [];
    this.disableHabilidadeTransferir = false;
    // this.disableOperador = true;
    //carregando dados da operacao (Fase - Capacidade da Linha)
    let operacao: any = [];

    this.itemsLinhaTransferir.forEach((elementLinha: any) => {
      elementLinha.linhaCapacidade.forEach((elementLinhaCapacidade: any) => {
        if (
          this.linhaSelecionadaTransferir.id == elementLinha.id &&
          this.linhaSelecionadaTransferir.nome == elementLinha.nome &&
          this.faseSelecionadaTransferir.id == elementLinhaCapacidade.id &&
          this.faseSelecionadaTransferir.fase == elementLinhaCapacidade.fase
        ) {
          operacao.push(elementLinhaCapacidade.processoOperacao);
        }
      });
    });

    this.itemsOperacaoTransferir = operacao;
  }

  //carregando a Habilidades da Fase selecionada a Transferir
  changeHabilidadesTransferir() {
    this.itemsHabilidadeTransferir = [];
    // this.disableOperador = false;

    //carregando dados da habilidade (Fase - Capacidade da Linha)
    let habilidades: any = [];

    this.itemsLinhaTransferir.forEach((elementLinha: any) => {
      elementLinha.linhaCapacidade.forEach((elementLinhaCapacidade: any) => {
        elementLinhaCapacidade.linhaCapacidadeHabilidades.forEach(
          (elementHabilidade: any) => {
            if (
              this.linhaSelecionadaTransferir.id == elementLinha.id &&
              this.linhaSelecionadaTransferir.nome == elementLinha.nome &&
              this.faseSelecionadaTransferir.id == elementLinhaCapacidade.id &&
              this.faseSelecionadaTransferir.fase ==
                elementLinhaCapacidade.fase &&
              this.operacaoSelecionadaTransferir.id ==
                elementLinhaCapacidade.processoOperacao.id &&
              this.operacaoSelecionadaTransferir.nome ==
                elementLinhaCapacidade.processoOperacao.nome
            ) {
              habilidades.push(elementHabilidade.processoHabilidade);
            }
          }
        );
      });
    });

    this.itemsHabilidadeTransferir = habilidades;
  }

  created() {
    this._controller = new LinhaTransferenciaFormController(this);
    this._controller.carregarDtos(this.$route.params.id);
    this.isMobile = Display.isMobile();
  }
}
