var validaNumeros = require("../../baseGenerica/validaNumeros.js");
var formatSegundos = require("../../baseGenerica/formatarSegundos.js");

import Vue from "vue";

import KLoading from "../../components/KLoading.vue";
import KDialog from "../../components/KDialog.vue";
import KDataList from "../../components/KDataList.vue";

import Component from "vue-class-component";
import { Watch } from "vue-property-decorator";

import { Display } from "../../configuration/display";
import { Storage } from "../../configuration/storage";

import { Header, HeaderIcon, HeaderAction } from "../../models/header";
import { Headers } from "../../models/headers";

import {
  IOrdemProducaoFormView,
  IOrdemProducaoFormController,
} from "../../contracts/OrdemProducao/OrdemProducaoFormContract";
import { OrdemProducaoFormController } from "../../controllers/OrdemProducao/OrdemProducaoFormController";

import { Validation, ValidationSchema } from "../../baseGenerica/validation";
import moment from "moment";

@Component({
  components: {
    KLoading,
    KDialog,
    KDataList,
  },
})
export default class OrdemProducaoForm
  extends Vue
  implements IOrdemProducaoFormView {
  _controller?: IOrdemProducaoFormController;

  itensTurno: any = [];

  expanded = [];
  singleExpand = false;

  caminho = [
    {
      text: "Home",
      disabled: false,
      href: "/",
    },
    {
      text: "Ordens de Produção",
      disabled: false,
      href: "/ordensProducao",
    },
  ];

  isMobile = false;

  isSucesso = false;

  dialogLoading = false;
  dialogMessage = false;
  textError = "";

  disabledCampos = true;

  //SNACKBAR
  snackbar = false;
  text = "";
  vertical = true;
  color = "";
  colorBorder = "";

  dialogAddOperadorSemHabilidade = false;
  operadoresSemHabilidade: any = [];

  headersSuporteLinha = new Headers([
    new Header(
      "Descrição da Categoria",
      "nomeCategoria",
      false,
      false,
      "left",
      false,
      "200"
    ),
    new Header(
      "Operador(es) Responsáveis",
      "operadores",
      false,
      false,
      "left",
      false,
      "200"
    ),
  ]);

  headersNovaLinha = [
    {
      text: "Operação",
      align: "center",
      sortable: false,
      width: 100,
      value: "",
    },
    {
      text: "Habilidade",
      align: "center",
      sortable: false,
      width: 100,
      value: "",
    },
    {
      text: "Operadores",
      align: "center",
      sortable: false,
      width: 400,
      value: "",
    },
    {
      //text: "Operadores sem habilidade",
      align: "center",
      sortable: false,
      width: 20,
      value: "",
    },
    {
      text: "Qtde de Operadores",
      align: "center",
      width: 100,
      sortable: false,
      value: "",
    },
    {
      text: "Tempo Produção",
      align: "center",
      sortable: false,
      width: 150,
      value: "",
    },
    {
      text: "Qtde Peças/Hora Por Operador",
      align: "center",
      sortable: false,
      width: 150,
      value: "",
    },
    {
      text: "Qtde Peças/Hora Produção",
      align: "center",
      sortable: false,
      width: 150,
      value: "",
    },
    {
      text: "Tempo Total",
      align: "center",
      sortable: false,
      width: 150,
      value: "",
    },
  ];

  headersLinha = [
    {
      text: "Nome",
      align: "start",
      sortable: false,
      value: "fase",
    },
    { text: "Ação", align: "right", value: "acao" },
    { text: "", align: "right", value: "data-table-expand" },
  ];

  itensCapacidadeLinha: any = [];
  itensSuporteLinha: any = [];
  itensOperadoresPorHabilidade: any = [];
  itensOperadores: any = [];

  idOrdemProducaoLinha = "";
  idOrdemProducaoPlano = "";

  ordemProducaoResponse: any = {};

  codigoDescricao = "";
  qtdeOperador = 0;
  quantidade = 0;
  nome = "";
  quantidadeOperador = 0;
  capacidadeProdutiva = 0;
  capacidadeProdutivaReal = 0;
  capacidadeProdutivaTurno = 0;
  numOrdemProducao = "-";
  producaoHoraHora = 0;
  qtdeOperadores = 0;

  turnoConvert: any = [];

  ordemProducao = {
    dataCadastro: new Date(),
    status: "S",
    segunda: false,
    terca: false,
    quarta: false,
    quinta: false,
    sexta: false,
    sabado: false,
    domingo: false,
    producao: 0,
    metaTurno: 0,
    capacidadeProdutivaReal: 0,
    capacidadeProdutivaTurno: 0,
    qtdeOperadores: 0,
    linha: {
      idLinha: 0,
      capacidadeLinha: this.itensCapacidadeLinha,
      recursoTecnicoLinha: this.itensSuporteLinha,
    },

    idOrdemProducao: 0,
    idOrdemProducaoLinha: 0,
    idProduto: 0,
    turnos: this.turnoConvert,
  };

  valid = true;

  customFilterOperador(item: any, queryText: any, itemText: any) {
    const textOne = item.nome.toLowerCase();
    const textTwo = item.nome.toLowerCase();
    const searchText = queryText.toLowerCase();

    return textOne.indexOf(searchText) > -1 || textTwo.indexOf(searchText) > -1;
  }

  carregaTurnos(itemsTurno: any): void {
    this.itensTurno = itemsTurno;

    this.itensTurno.forEach((element: any) => {
      element.textoDescHora =
        element.descricao + " - HORAS TOTAL: " + element.horaTotal;
    });
  }

  apenasNumeros(event: any) {
    validaNumeros(event);
  }

  loading(visible: boolean): void {
    this.dialogLoading = visible;
  }

  fecharDialogMessage() {
    this.dialogMessage = false;
    if (this.isSucesso) {
      this.$router.push("/ordensProducao");
    }
  }

  retornaListaOrdensProducao() {
    this.$router.push("/ordensProducao");
  }

  retornaListaOrdensProducaoSimulacao() {
    this.$router.push("/ordensProducao");
  }

  showMsg(msg: string, modo: number): void {
    this.textError = msg;
    if (modo == 1) {
      this.isSucesso = true;
    }
    this.dialogMessage = true;
  }

  validate() {
    if (
      this.ordemProducao.turnos == null ||
      this.ordemProducao.turnos.length == 0
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text =
        "Selecione o Turno em que deseja realizar a Ordem de Produção";
      this.snackbar = true;
      return true;
    }

    if (
      this.ordemProducao.segunda == false &&
      this.ordemProducao.terca == false &&
      this.ordemProducao.quarta == false &&
      this.ordemProducao.quinta == false &&
      this.ordemProducao.sexta == false &&
      this.ordemProducao.sabado == false &&
      this.ordemProducao.domingo == false
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text =
        "Selecione ao menos um dia da semana para realizar a Ordem de Produção";
      this.snackbar = true;
      return true;
    }

    //verifica se ha operadores para a capacidade de linha
    for (let i = 0; i < this.itensCapacidadeLinha.length; i++) {
      let verificador = false;
      for (
        let j = 0;
        j < this.itensCapacidadeLinha[i].linhaCapacidadeHabilidades.length;
        j++
      ) {
        if (
          this.itensCapacidadeLinha[i].linhaCapacidadeHabilidades[j]
            .operadores == null
        ) {
          this.colorBorder = "error";
          this.color = "error";
          this.text =
            "Selecione o(s) operador(es) da Capacidade de Linha: " +
            this.itensCapacidadeLinha[i].processoDTOOperacao.nome;
          this.snackbar = true;
          verificador = true;
          break;
        }
      }

      if (verificador == true) {
        return true;
        break;
      }
    }

    return false;
  }

  saveOrdemProducao(tipo: string) {
    this.ordemProducao.linha.idLinha = this.ordemProducaoResponse.linha.id;
    this.ordemProducao.linha.capacidadeLinha = this.itensCapacidadeLinha;
    //this.ordemProducao.linha.recursoTecnicoLinha = this.itensSuporteLinha;
    this.ordemProducao.idOrdemProducao = this.ordemProducaoResponse.ordemProducao.id;
    this.ordemProducao.idOrdemProducaoLinha = this.ordemProducaoResponse.ordemProducaoLinha.id;
    this.ordemProducao.idProduto = this.ordemProducaoResponse.produto.id;
    this.ordemProducao.producao = this.ordemProducaoResponse.ordemProducao.quantidade;
    this.ordemProducao.capacidadeProdutivaReal = this.capacidadeProdutivaReal;
    this.ordemProducao.capacidadeProdutivaTurno = this.capacidadeProdutivaTurno;
    this.ordemProducao.qtdeOperadores = this.qtdeOperadores;

    this.ordemProducao.status = tipo == "S" ? "S" : "P";

    if (!this.validate()) {
      this._controller!.salvar(this.ordemProducao);
      // this._controller!.update(this.idOrdemProducao, this.ordemProducao);
      //   this.retornaListaOrdensProducao();
    }
  }

  updateOrdemProducao() {
    this.ordemProducao.linha.idLinha = this.ordemProducaoResponse.linha.id;
    this.ordemProducao.linha.capacidadeLinha = this.itensCapacidadeLinha;
    //this.ordemProducao.linha.recursoTecnicoLinha = this.itensSuporteLinha;
    this.ordemProducao.idOrdemProducao = this.ordemProducaoResponse.ordemProducao.id;
    this.ordemProducao.idOrdemProducaoLinha = this.ordemProducaoResponse.ordemProducaoLinha.id;
    this.ordemProducao.idProduto = this.ordemProducaoResponse.produto.id;
    this.ordemProducao.producao = this.ordemProducaoResponse.ordemProducao.quantidade;

    // if (!this.validate()) {
    // } else {
    //this._controller!.salvar(this.ordemProducao);
    //this._controller!.update(this.idOrdemProducao, this.ordemProducao);
    //   this.retornaListaOrdensProducao();
    // }
  }

  // calcularQtdOperadores(){

  //   let horasSegundos = 3600;

  //   let tempoProducaoPecas = this.producaoHoraHora == 0 ? 0 : horasSegundos/this.producaoHoraHora;

  //   this.ordemProducao.qtdeOperadores = this.producaoHoraHora == 0 ? 0 : Math.ceil(this.producaoHoraHora / tempoProducaoPecas);

  // }

  calculaMetaTurno() {
    // console.log(this.ordemProducao.turnos.horaTotal);

    let calculoMetaTurno = 0;

    let newTimeConvert = moment(
      this.ordemProducao.turnos.horaTotal,
      "HH:mm:ss"
    );

    let newTime = newTimeConvert.toDate();

    calculoMetaTurno = this.formatarSegundos(
      newTime.getHours(),
      newTime.getMinutes(),
      newTime.getSeconds()
    );

    let meta: any;

    meta = (this.producaoHoraHora * (calculoMetaTurno / 60 / 60)).toFixed(2);

    this.ordemProducao.metaTurno =
      this.producaoHoraHora > 0 ? parseFloat(meta) : 0;

    //CALCULO CAPACIDADE PRODUTIVA TURNO
    let capacidadeProdutivaTurno = (
      this.capacidadeProdutivaReal *
      (calculoMetaTurno / 60 / 60)
    ).toFixed(2);

    this.capacidadeProdutivaTurno =
      this.capacidadeProdutivaReal > 0
        ? parseFloat(capacidadeProdutivaTurno)
        : 0;
  }

  formatarSegundos(h: any, min: any, s: any) {
    return formatSegundos(h, min, s);
  }

  carregarDtos(): void {
    this.idOrdemProducaoLinha = this.$route.params.idOrdemProducaoLinha;
    this.idOrdemProducaoPlano = this.$route.params.idOrdemProducaoPlano;

    if (this.$route.params.idOrdemProducaoPlano) {
      this.disabledCampos = true;
    } else {
      this.disabledCampos = false;
    }
  }

  showOrdemProducaoForEditar(item: any) {
    this.loading(true);
    this.ordemProducaoResponse = item;

    this.itensCapacidadeLinha = this.ordemProducaoResponse.linha.linhaCapacidade;

    let itensSuporteLinha = this.ordemProducaoResponse.linhaProducao.linhaProducaoCategorias;

    this.ordemProducao = this.ordemProducaoResponse.ordemProducaoPlano;

    this.ordemProducao.turnos = this.ordemProducaoResponse.ordemProducaoTurnos[0];

    this.codigoDescricao =
      this.ordemProducaoResponse.produto.codigo.toString().trim() +
      " - " +
      this.ordemProducaoResponse.produto.descricao.toString().trim();
    this.qtdeOperador = this.ordemProducaoResponse.produto.qtdeOperador;
    this.producaoHoraHora = this.ordemProducaoResponse.produto.producaoHoraHora;
    this.quantidade = this.ordemProducaoResponse.ordemProducao.quantidade;
    this.nome = this.ordemProducaoResponse.linhaProducao.nome;
    this.quantidadeOperador = this.ordemProducaoResponse.linha.quantidadeOperador;
    this.capacidadeProdutiva = this.ordemProducaoResponse.linha.capacidadeProdutiva;
    this.numOrdemProducao = this.ordemProducaoResponse.ordemProducao.numOrdemProducao;
    this.capacidadeProdutivaReal = this.ordemProducaoResponse.ordemProducaoPlano.capacidadeProdutivaReal;
    this.qtdeOperadores = this.ordemProducaoResponse.ordemProducaoPlano.qtdeOperadores;
    this.capacidadeProdutivaTurno = this.ordemProducaoResponse.ordemProducaoPlano.capacidadeProdutivaTurno;
    //console.log(this.quantidade);
    //console.log(this.producaoHoraHora);

    //instanciando oeperadores em capacidade da linha
    this.itensCapacidadeLinha.forEach((element: any) => {
      element.linhaCapacidadeHabilidades.forEach((element2: any) => {
        element2.operadores = [];
      });
    });

    //convertendo time para time imput
    this.itensCapacidadeLinha.forEach((elementCapLinha: any) => {
      elementCapLinha.linhaCapacidadeHabilidades.forEach(
        (elementCapLinhaHabilidade: any) => {
          elementCapLinhaHabilidade.time = new Date(
            elementCapLinhaHabilidade.time
          )
            .toLocaleTimeString()
            .toString();

          elementCapLinhaHabilidade.ordemProducaoOperadorLinhaCapacidade.forEach(
            (element: any) => {
              elementCapLinhaHabilidade.operadores.push(
                element.funcionarioErpDTO
              );
            }
          );
        }
      );
    });

    //Supore de Linha
    itensSuporteLinha.forEach((elementSuporteLinha: any) => {
      if(elementSuporteLinha.deletado == "N"){
        this.itensSuporteLinha.push(elementSuporteLinha)
      }
    });

    this.loading(false);
  }

  showOrdemProducaoForNovo(item: any) {
    this.loading(true);
    this.ordemProducaoResponse = item;

    this.itensCapacidadeLinha = this.ordemProducaoResponse.linha.linhaCapacidade;

    let itensSuporteLinha = this.ordemProducaoResponse.linhaProducao.linhaProducaoCategorias;

    this.codigoDescricao =
      this.ordemProducaoResponse.produto.codigo.toString().trim() +
      " - " +
      this.ordemProducaoResponse.produto.descricao.toString().trim();
    this.qtdeOperador = this.ordemProducaoResponse.produto.qtdeOperador;
    this.producaoHoraHora = this.ordemProducaoResponse.produto.producaoHoraHora;
    this.quantidade = this.ordemProducaoResponse.ordemProducao.quantidade;
    this.nome = this.ordemProducaoResponse.linhaProducao.nome;
    this.quantidadeOperador = this.ordemProducaoResponse.linha.quantidadeOperador;
    this.capacidadeProdutiva = this.ordemProducaoResponse.linha.capacidadeProdutiva;
    this.numOrdemProducao = this.ordemProducaoResponse.ordemProducao.numOrdemProducao;

    //CALCULO DA CAPACIDADE PRODUTIVA REAL/HORA
    let horasSegundos = 3600;
    let tempoProducaoPecas =
      this.producaoHoraHora == 0 ? 0 : horasSegundos / this.producaoHoraHora;
    let capacidadeProdutivaReal =
      tempoProducaoPecas == 0
        ? 0
        : tempoProducaoPecas * this.quantidadeOperador;
    this.capacidadeProdutivaReal = parseFloat(
      capacidadeProdutivaReal.toFixed(2)
    );

    // CALCULO QTDE DE OPERADORES NECESSARIOS
    this.qtdeOperadores =
      this.producaoHoraHora == 0
        ? 0
        : Math.ceil(this.producaoHoraHora / tempoProducaoPecas);

    //instanciando oeperadores em capacidade da linha
    this.itensCapacidadeLinha.forEach((element: any) => {
      element.linhaCapacidadeHabilidades.forEach((element2: any) => {
        element2.operadores = null;
      });
    });

    //convertendo time para time imput
    this.itensCapacidadeLinha.forEach((elementCapLinha: any) => {
      elementCapLinha.linhaCapacidadeHabilidades.forEach(
        (elementCapLinhaHabilidade: any) => {
          elementCapLinhaHabilidade.time = new Date(
            elementCapLinhaHabilidade.time
          )
            .toLocaleTimeString()
            .toString();
        }
      );
    });

    //retirando os suporte de linha com deletado == "S"
    itensSuporteLinha.forEach((elementSuporteLinha: any) => {
      if (elementSuporteLinha.deletado == "N"){
        this.itensSuporteLinha.push(elementSuporteLinha);
      }
    });

    //abatascendo operadores por habilidade
    let i = 0;
    this.itensCapacidadeLinha.forEach((elementCapLinha: any) => {
      i = 0;
      elementCapLinha.linhaCapacidadeHabilidades.forEach(
        (elementCapLinhaHabilidade: any) => {
          this.itensOperadoresPorHabilidade.forEach(
            (elemOpPorHabilidade: any) => {
              if (
                elementCapLinhaHabilidade.idProcessoHabilidade !==
                elemOpPorHabilidade.idProcessoHabilidade
              ) {
                this.itensOperadoresPorHabilidade.splice(i, 1);
              }
              i++;
            }
          );
        }
      );
    });

    this.loading(false);
  }

  carregaOperadoresPorHabilidade(operadores: any): void {
    this.itensOperadoresPorHabilidade = operadores;
  }

  carregaOperadores(operadores: any): void {
    this.itensOperadores = operadores;

    if (this.$route.params.idOrdemProducaoPlano) {
      this.itensOperadoresPorHabilidade = operadores;
    }
  }
  modalOperadorSemHabilidade() {
    this.operadoresSemHabilidade = [];
    this.dialogAddOperadorSemHabilidade = true;
  }
  addOpeSemHabilidade() {
    this.itensOperadoresPorHabilidade.forEach((element: any) => {
      if (
        element.id !== this.operadoresSemHabilidade.id &&
        element.nome !== this.operadoresSemHabilidade.nome
      ) {
        this.itensOperadoresPorHabilidade.push(this.operadoresSemHabilidade);
      }
    });

    this.dialogAddOperadorSemHabilidade = false;
  }

  created() {
    this._controller = new OrdemProducaoFormController(this);
    this._controller.carregarDtos(
      this.$route.params.idOrdemProducaoLinha,
      this.$route.params.idOrdemProducaoPlano
    );
    this.carregarDtos();
    this.isMobile = Display.isMobile();
  }
}
