  //TIPO: O - OPERAÇÃO /H - HABILIDADE
  addProcesso(tipo: string) {
    this.tipo = tipo;
    this.titulo =
      tipo == "O"
        ? "Nova Operação"
        : tipo == "H"
        ? "Nova Habilidade"
        : "Nova Fase";
    this.placeholderProcesso =
      tipo == "O"
        ? "Descrição da Operação"
        : tipo == "H"
