    //   this.snackbar = true;
    //   return;
    // }

    if (
      this.descricaoProcesso == "" ||
      this.descricaoProcesso.length == 0 ||
      this.descricaoProcesso == null
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text =
        this.tipo == "O"
          ? "Informe a descrição da Operação."
          : this.tipo == "H"
          ? "Informe a descrição da Habilidade."
          : "Informe a descrição da Fase";

      this.snackbar = true;
      return;
    }

    let processo = {
      codigo: this.codigoProcesso,
      nome: this.descricaoProcesso,
      status: "A", //STATUS: A: ATIVO / I: INATIVO
      dataCadastro: new Date(),
      tipo: this.tipo,
    };

    this._controller!.salvarProcesso(processo);
  }

  calculaOperadorXTime(tipo: string) {
    //capacidade linha
    if (tipo == "CL") {
      this.itensCadCapacidadeLinha.forEach((element: any) => {
        let calculoOperadorXtime = 0;
        if (
          element.time != "" &&
          element.time != undefined &&
          element.time != null &&
          element.qtdeOperador != 0 &&
          element.qtdeOperador != "" &&
          element.qtdeOperador != undefined
        ) {
          //CONVERTENDO
          let newTimeConvert = moment(element.time, "HH:mm:ss");

          let newTime = newTimeConvert.toDate();

          calculoOperadorXtime += this.formatarSegundos(
            newTime.getHours(),
            newTime.getMinutes(),
            newTime.getSeconds()
          );

          if (
            element.qtdeOperador > 0 &&
            element.qtdeOperador !== null &&
            element.qtdeOperador !== ""
          ) {
            calculoOperadorXtime = calculoOperadorXtime / element.qtdeOperador;

            element.qtdePecaMinutoOperador =
              60 / (calculoOperadorXtime / 60) / element.qtdeOperador; //60 segundos = 1 minuto em segundos
            element.qtdePecaOperadorProducao = 60 / (calculoOperadorXtime / 60);
          }

          element.operadorXtime = this.dataFormatarSoma(calculoOperadorXtime);
        }
      });
    }

    // if (tipo == "RT") {
    //   this.itensCadRecursosTecnicos.forEach((element: any) => {
    //     let calculoOperadorXtime = 0;
    //     if (
    //       element.time != "" &&
    //       element.time != undefined &&
