    //       );

    //       calculoOperadorXtime = calculoOperadorXtime * element.qtdeOperador;

    //       element.operadorXtime = this.dataFormatarSoma(calculoOperadorXtime);
    //     }
    //   });
    //}
  }

  deleteCapacidadeLinhaConfirmado() {
    this.dialogDelete = false;
    this._controller!.deleteLinhaCapacidadeHabilidadeItem(
      this.itemDelete.id,
      parseInt(this.idLinha)
    );
    this.itemDelete = [];
  }

  deleteRecursoTecnicoLinhaConfirmado() {
    this.dialogDeleteRecurso = false;
    this._controller!.deleteRecursoTecnicoHabilidadeItem(
      this.itemDelete.id,
      parseInt(this.idLinha)
    );
    this.itemDelete = [];
  }

  deleteCapacidadeLinhaCancelar() {
