    //CAPACIDADE LINHA
    this.dialog = false;
    this.itensCadCapacidadeLinha = [];
    this.cadCapacidadeLinha = {
      fase: "",
      operacao: [],
      habilidades: [],
    };

    //RECURSO TECNICO
    this.dialogRecursoTecnico = false;
    this.itensCadRecursosTecnicos = [];
    this.cadRecursosTecnicos = {
      fase: "",
      operacao: [],
      habilidades: [],
    };
  }

  saveRecursoTecnicoLinha() {
    if (!this.validateSaveRecursoTecnicoLinha()) {
      // this.itensCadRecursosTecnicos.forEach((element: any) => {
      //   let timeConvert = moment(element.time, "HH:mm:ss");

      //   let newTime = timeConvert.toDate();

      //   element.time = newTime.toLocaleTimeString().toString();
      // });

      let recursosTecnicos: any = {
        idFase: this.cadRecursosTecnicos.fase.id,
        idOperacao: this.cadRecursosTecnicos.operacao.id,
        dataCadastro: new Date(),
        deletado: "N",
        itens: this.itensCadRecursosTecnicos,
        idLinha: this.idLinha,
      };

      this._controller!.salvarRecursoTecnico(recursosTecnicos, this.idLinha);
      this.dialogRecursoTecnico = false;
    }
  }

  saveCapacidadeLinha() {
    if (!this.validateSaveCapacidadeLinha()) {
      this.itensCadCapacidadeLinha.forEach((element: any) => {
        let timeConvert = moment(element.time, "HH:mm:ss");

        let newTime = timeConvert.toDate();

        element.time = newTime.toLocaleTimeString().toString();
      });

      let capacidadeLinha: any = {
        idFase: this.cadCapacidadeLinha.fase.id,
        idOperacao: this.cadCapacidadeLinha.operacao.id,
        dataCadastro: new Date(),
        deletado: "N",
        itens: this.itensCadCapacidadeLinha,
        idLinha: this.idLinha,
      };

      this._controller!.salvarCapacidadeLinha(capacidadeLinha, this.idLinha);
      this.dialog = false;
    }
  }

  addCadHabilidadesLinha() {
    if (!this.validateCapacidadeLinha()) {
      this.itensCadCapacidadeLinha = [];
      let cont = 0;
      this.cadCapacidadeLinha.habilidades.forEach((element: any) => {
        let capacidadeLinha: any = {};
        cont++;
        capacidadeLinha.idOperacao = this.cadCapacidadeLinha.operacao.id;
        capacidadeLinha.nomeOperacao = this.cadCapacidadeLinha.operacao.nome;
        capacidadeLinha.idHabilidade = element.id;
        capacidadeLinha.nomeHabilidade = element.nome;
        capacidadeLinha.qtdeOperador = null;
        capacidadeLinha.nomeRef = "menuRef" + cont;
        capacidadeLinha.time = null;
        capacidadeLinha.qtdePecaMinutoOperador = "";
        capacidadeLinha.qtdePecaOperadorProducao = "";
        capacidadeLinha.menu = false;
        capacidadeLinha.menu2 = false;
        capacidadeLinha.modal = false;

        this.itensCadCapacidadeLinha.push(capacidadeLinha);
      });
    }

    this.cadCapacidadeLinha = {
      fase: this.cadCapacidadeLinha.fase,
      operacao: this.cadCapacidadeLinha.operacao,
      habilidades: [],
    };
  }

  addCadRecursoTecnicoLinha() {
    if (!this.validateRecursoTecnico()) {
      this.itensCadRecursosTecnicos = [];
      let cont = 0;
      this.cadRecursosTecnicos.habilidades.forEach((element: any) => {
        let recursoTecnicoLinha: any = {};
        cont++;
        recursoTecnicoLinha.idFase = this.cadRecursosTecnicos.fase.id;
        recursoTecnicoLinha.idOperacao = this.cadRecursosTecnicos.operacao.id;
        recursoTecnicoLinha.nomeOperacao = this.cadRecursosTecnicos.operacao.nome;
        recursoTecnicoLinha.idHabilidade = element.id;
        recursoTecnicoLinha.nomeHabilidade = element.nome;
        recursoTecnicoLinha.qtdeOperador = null;
        //recursoTecnicoLinha.nomeRef = "menuRef" + cont;
        // recursoTecnicoLinha.time = null;
        //recursoTecnicoLinha.menu = false;
