    this.itensCadRecursosTecnicos = [];
    this.cadRecursosTecnicos = {
      fase: "",
      operacao: [],
      habilidades: [],
    };
    this.dialogRecursoTecnico = true;
  }

  apenasNumeros(event: any) {
    validaNumeros(event);

    this.somaQtdeOperadoresCapacidadeProdutiva();
  }

  loading(visible: boolean): void {
    this.dialogLoading = visible;
  }

  fecharDialogMessage() {
    this.dialogMessage = false;
    if (this.isSucesso) {
      this.$router.push("/Linhas");
    }
  }

  cancelarLinha() {
    this._controller!.deleteitem(parseInt(this.idLinha));
  }

  retornaListaLinhas(): void {
    this.$router.push("/Linhas");
  }

  showMsg(msg: string, modo: number): void {
    this.textError = msg;
    if (modo == 1) {
      this.isSucesso = true;
    }
    this.dialogMessage = true;
  }

  validateSaveRecursoTecnicoLinha() {
    let retorno = false;

    if (this.itensCadRecursosTecnicos.length == 0) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Sem Operações e Habilidades selecionadas para salvar dados.";
      this.snackbar = true;
      retorno = true;
    }

    this.itensCadRecursosTecnicos.forEach((element: any) => {
      if (
        parseInt(element.qtdeOperador) == 0 ||
        parseInt(element.qtdeOperador) == null ||
        parseInt(element.qtdeOperador) < 0 ||
        element.qtdeOperador == ""
      ) {
        this.colorBorder = "error";
        this.color = "error";
        this.text =
          "O campo quantidade de operadores da habilidade " +
          element.nomeHabilidade +
          " não pode ser 0 (zero) ou inferior a 0 (zero).";
        this.snackbar = true;
        retorno = true;
      }

      // if (element.time == "00:00" || element.time == null) {
      //   this.colorBorder = "error";
      //   this.color = "error";
      //   this.text =
      //     "O campo Time da habilidade " +
      //     element.nomeHabilidade +
      //     " não pode ser 00:00";
      //   this.snackbar = true;
      //   retorno = true;
      // }
    });
    return retorno;
  }

