    let retorno = false;

    if (this.itensCadCapacidadeLinha.length == 0) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Sem Operações e Habilidades selecionadas para salvar dados.";
      this.snackbar = true;
      retorno = true;
    }

    this.itensCadCapacidadeLinha.forEach((element: any) => {
