var validaNumeros = require("../../baseGenerica/validaNumeros.js");

import Vue from "vue";

import KLoading from "../../components/KLoading.vue";
import KDialog from "../../components/KDialog.vue";
import KDataList from "../../components/KDataList.vue";

import Component from "vue-class-component";
import { Watch } from "vue-property-decorator";

import { Display } from "../../configuration/display";
import { Storage } from "../../configuration/storage";

import { Header, HeaderIcon, HeaderAction } from "../../models/header";
import { Headers } from "../../models/headers";

import {
  ILinhaProducaoFormView,
  ILinhaProducaoFormController,
} from "../../contracts/Cadastro/LinhaProducaoFormContract";
import { LinhaProducaoFormController } from "../../controllers/Cadastro/LinhaProducaoFormController";

@Component({
  components: {
    KLoading,
    KDialog,
    KDataList,
  },
})
export default class LinhaProducaoForm
  extends Vue
  implements ILinhaProducaoFormView {
  _controller?: ILinhaProducaoFormController;

  caminho = [
    {
      text: "Home",
      disabled: false,
      href: "/",
    },
    {
      text: "Linha de Produção",
      disabled: false,
      href: "/linhasProducao",
    },
  ];

  //validate form
  valid = true;
  lazy = false;

  isMobile = false;

  isSucesso = false;

  dialogLoading = false;
  dialogDelete = false;
  dialogDeleteCategoria = false;
  dialogMessage = false;
  textError = "";

  itemsFases: any = [];
  itemsCategorias: any = [];
  itemsOperadores: any = [];

  idLinhaProducao = "";

  categoriasEdit: any = [];
  linhaProducao: any = {
    id: 0,
    nome: "",
    qtdeCapacidadeOperadoresLinha: 0,
    fases: [],
    categorias: [],
    dataCadastro: new Date(),
    deletado: "N",
  };

  fases: any = {
    processoDTO: [],
    qtdeOperador: null,
    dataCadastro: new Date(),
    deletado: "N",
  };

  categorias: any = {
    processoDTO: [],
    funcionarioErpDTO: [],
    dataCadastro: new Date(),
    deletado: "N",
  };

  //SNACKBAR
  snackbar = false;
  text = "";
  vertical = true;
  color = "";
  colorBorder = "";

  headers = new Headers([]);
  headersSuporteLinha = new Headers([
    new Header(
      "Descrição da Categoria",
      "nomeCategoria",
      false,
      false,
      "left",
      false,
      "200"
    ),
    new Header(
      "Operador(es) Responsáveis",
      "operadores",
      false,
      false,
      "left",
      false,
      "200"
    ),
    new Header("Ação", "acao", false, false, "center", false, "100"),
  ]);

  indexRemoverFase = 0;
  indexRemoverCategoria = 0;

  removerFase() {
    for (const { item, index } of this.linhaProducao.fases.map(
      (item: any, index: any) => ({
        item,
        index,
      })
    )) {
      if (index == this.indexRemoverFase) {
        this.linhaProducao.fases.splice(index, 1);
        this.indexRemoverFase = -1;
        this.dialogDelete = false;
        return;
      }
    }
  }

  removerCategoriaEdit() {
    //console.log(this.linhaProducao.categorias)
    for (const { item, index } of this.linhaProducao.categorias.map(
      (item: any, index: any) => ({
        item,
        index,
      })
    )) {
      if (index == this.indexRemoverCategoria) {
        //this.linhaProducao.categorias.splice(index, 1);
        item.deletado = "S"
        this.indexRemoverCategoria = -1;
        this.dialogDeleteCategoria = false;
        //return;
      }
    }

    this.categoriasEdit = this.linhaProducao.categorias;
    this.linhaProducao.categorias = [];
    this.categoriasEdit.forEach((element: any) => {
      if (element.deletado == 'N'){
        this.linhaProducao.categorias.push(element)

      }
    });
  }

  removerCategoriaNovo() {
    //console.log(this.linhaProducao.categorias)
    for (const { item, index } of this.linhaProducao.categorias.map(
      (item: any, index: any) => ({
        item,
        index,
      })
    )) {
      if (index == this.indexRemoverCategoria) {
        this.linhaProducao.categorias.splice(index, 1);
        this.indexRemoverCategoria = -1;
        this.dialogDeleteCategoria = false;
        return;
      }
    }
  }


  modalRemoverFase(indexRemoveItem: string) {
    this.indexRemoverFase = parseInt(indexRemoveItem);
    this.dialogDelete = true;
  }

  modalRemoverCategoria(indexRemoveItem: string) {
    this.indexRemoverCategoria = parseInt(indexRemoveItem);
    this.dialogDeleteCategoria = true;
    //(this.indexRemoverCategoria)
  }

  ativarDesativarFase(item: any) {
    //console.log(item);
    this.linhaProducao.fases.forEach((element: any) => {
      if (
        element.processoDTO.id == item.processoDTO.id &&
        element.deletado == "N"
      ) {
        element.deletado = "S";
        this.colorBorder = "#1867C0";
        this.color = "#1867C0";
        this.text =
          "A Fase " +
          element.processoDTO.nome +
          " será DESATIVADA quando Salvar os dados.";
        this.snackbar = true;
      } else if (
        element.processoDTO.id == item.processoDTO.id &&
        element.deletado == "S"
      ) {
        element.deletado = "N";
        this.colorBorder = "#1867C0";
        this.color = "#1867C0";
        this.text =
          "A Fase " +
          element.processoDTO.nome +
          " será novamente ATIVADA quando Salvar os dados.";
        this.snackbar = true;
      }
    });
  }

  cancelaRemoverFase() {
    this.indexRemoverFase = -1;
    this.dialogDelete = false;
  }

  cancelaRemoverCategoria() {
    this.indexRemoverCategoria = -1;
    this.dialogDeleteCategoria = false;
  }


  apenasNumeros(event: any) {
    validaNumeros(event);
  }

  loading(visible: boolean): void {
    this.dialogLoading = visible;
  }

  fecharDialogMessage() {
    this.dialogMessage = false;
    if (this.isSucesso) {
      this.$router.push("/linhasProducao");
    }
  }

  retornaListaLinhaProducao() {
    this.$router.push("/linhasProducao");
  }

  showMsg(msg: string, modo: number): void {
    this.textError = msg;
    if (modo == 1) {
      this.isSucesso = true;
    }
    this.dialogMessage = true;
  }

  validate() {
    if (
      this.linhaProducao.nome.length == 0 ||
      this.linhaProducao.nome == null ||
      this.linhaProducao.nome == ""
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Informe o Nome da Linha de Produção.";
      this.snackbar = true;
      return true;
    }

    if (this.linhaProducao.fases.length == 0) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "É necessário inserir as Fases da Linha.";
      this.snackbar = true;
      return true;
    }
  }

  saveLinhaProducao() {
    if (this.validate()) {
    } else {
      //this.solicitacao.user = parseInt(Storage.get("User")!);
      this._controller!.salvar(this.linhaProducao);
    }
  }

  updateLinhaProducao() {
    if (this.validate()) {
    } else {
      //this.solicitacao.user = parseInt(Storage.get("User")!);
      this.linhaProducao.categorias = [];
      this.linhaProducao.categorias = this.categoriasEdit;
      this._controller!.update(this.idLinhaProducao, this.linhaProducao);
    }
  }

  carregaFuncionarioLocalLinhaProducao(operadores: any): void {
    this.itemsOperadores = operadores;
  }

  carregarDtos(): void {
    this.idLinhaProducao = this.$route.params.id;

    if (!this.idLinhaProducao) {
      this.headers = new Headers([
        new Header(
          "Descrição da Fase",
          "nomeFase",
          false,
          false,
          "center",
          false,
          "200"
        ),
        new Header(
          "Capacidade de Operador",
          "qtdeOperador",
          false,
          false,
          "center",
          false,
          "100"
        ),
        new Header("Ação", "acao", false, false, "center", false, "100"),
      ]);
    } else {
      this.headers = new Headers([
        new Header("Status", "deletado", false, false, "center", false, "100"),
        new Header(
          "Descrição da Fase",
          "nomeFase",
          false,
          false,
          "center",
          false,
          "200"
        ),
        new Header(
          "Capacidade de Operador",
          "qtdeOperador",
          false,
          false,
          "center",
          false,
          "100"
        ),
        new Header("Ação", "acao", false, false, "center", false, "100"),
      ]);
    }
  }

  carregaProcessos(itemsFases: any, itemsCategorias: any): void {
    this.itemsFases = itemsFases;
    this.itemsCategorias = itemsCategorias;
  }

  showLinhaProducaoForSave(item: any) {
    this.linhaProducao.nome = item.nome;
    this.linhaProducao.descricao = item.descricao;
  }

  showLinhaProducaoForEditar(item: any) {
    
    this.linhaProducao.nome = item.nome;
    this.linhaProducao.qtdeCapacidadeOperadoresLinha =
      item.qtdeCapacidadeOperadoresLinha;

    item.linhaProducaoFases.forEach((element: any) => {
      element.qtdeOperador = element.qtdeOperador.toString();
      this.linhaProducao.fases.push(element);
    });

    item.linhaProducaoCategorias.forEach((element: any) => {
      
      let categoria: any = {
        processoDTO: element.processoDTO,
        funcionarioErpDTO: [],
        dataCadastro: element.dataCadastro,
        deletado: element.deletado,
      };
      element.linhaProducaoCategoriasFuncionarioLocal.forEach((elementFunc: any) => {
        categoria.funcionarioErpDTO.push(elementFunc.funcionarioErpDTO)
      });
    
     if (element.deletado == 'N'){
       this.linhaProducao.categorias.push(categoria)
     }
    });

    this.categoriasEdit = this.linhaProducao.categorias
  }

  carregaSetores(item: any) {
    //CARREGANDO DADOS DO FUNCIONARIO
    this.itemsFases = item;
  }

  validateAddFase() {
    if (this.fases.processoDTO.length == 0) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a fase para adicionar.";
      this.snackbar = true;
      return true;
    } else if (
      this.fases.qtdeOperador == null ||
      parseInt(this.fases.qtdeOperador) <= 0
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text =
        "Informe um valor válido para o campo Quantidade de Operadores por Fase.";
      this.snackbar = true;
      return true;
    } else {
      let flag = false;
      this.linhaProducao.fases.forEach((element: any) => {
        if (element.processoDTO.id == this.fases.processoDTO.id) {
          this.colorBorder = "warning";
          this.color = "warning";
          this.text = "Fase já adicionada.";
          this.fases = {
            processoDTO: [],
            qtdeOperador: null,
            dataCadastro: new Date(),
            deletado: "N",
          };
          this.snackbar = true;
          flag = true;
        }
      });
      return flag;
    }

    return false;
  }

  validateAddCategoria() {
    if (this.categorias.processoDTO.length == 0) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a Categoria do Suporte de Linha para adicionar.";
      this.snackbar = true;
      return true;
    } else if (
      this.categorias.funcionarioErpDTO == null ||
      parseInt(this.categorias.funcionarioErpDTO) <= 0
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text =
        "Informe o/os operador(es) para adicionar a Categoria de Suporte de Linha.";
      this.snackbar = true;
      return true;
    } else {
      let flag = false;
      this.linhaProducao.categorias.forEach((element: any) => {
        if (element.processoDTO.id == this.categorias.processoDTO.id) {
          this.colorBorder = "warning";
          this.color = "warning";
          this.text = "Categoria de Suporte de Linha já adicionada.";
          this.categorias = {
            processoDTO: [],
            funcionarioErpDTO: [],
            dataCadastro: new Date(),
            deletado: "N",
          };
          this.snackbar = true;
          flag = true;
        }
      });
      return flag;
    }

    return false;
  }

  addSolicitacao() {
    this.$router.push("/linhasProducao");
  }

  customFilterSetor(item: any, queryText: any, itemText: any) {
    const textOne = item.sigla.toLowerCase();
    const textTwo = item.descricao.toLowerCase();
    const searchText = queryText.toLowerCase();

    return textOne.indexOf(searchText) > -1 || textTwo.indexOf(searchText) > -1;
  }

  somaOperadoresFases() {
    this.linhaProducao.qtdeCapacidadeOperadoresLinha = 0;
    this.linhaProducao.fases.forEach((element: any) => {
      this.linhaProducao.qtdeCapacidadeOperadoresLinha += parseInt(
        element.qtdeOperador
      );
    });
  }

  addFase() {
    if (this.validateAddFase()) {
    } else {
      this.linhaProducao.fases.push(this.fases);
      this.somaOperadoresFases();
      this.fases = {
        processoDTO: [],
        qtdeOperador: null,
        dataCadastro: new Date(),
        deletado: "N",
      };
    }
  }

  addCategoria() {
    if (this.validateAddCategoria()) {
    } else {
      this.linhaProducao.categorias.push(this.categorias);
      this.categorias = {
        processoDTO: [],
        FuncionarioErpDTO: [],
        dataCadastro: new Date(),
        deletado: "N",
      };
    }

    //console.log(this.linhaProducao.categorias);
  }

  created() {
    this._controller = new LinhaProducaoFormController(this);
    this._controller.carregarDtos(this.$route.params.id);
    this.carregarDtos();
    this.isMobile = Display.isMobile();
  }
}
