import { ISaidaAntecipadaListaView, ISaidaAntecipadaListaController } from "../../contracts/SaidaAntecipada/SaidaAntecipadaListaContract";
import axios from "../../plugins/axios"

import { Storage } from "../../configuration/storage"
import { Header, HeaderIcon, HeaderAction } from "../../models/header";
import { Headers } from "../../models/headers";

export class SaidaAntecipadaListaController implements ISaidaAntecipadaListaController {

    _view: ISaidaAntecipadaListaView

    constructor(view: ISaidaAntecipadaListaView) {
        this._view = view
    }

    carregarItens(): void {
        this._view.loading(true)

        this.carregarTurnos();

        axios
            .get('SaidaAntecipada')
            .then((response: any) => {

                response.data.forEach((element: { acoes: HeaderAction[]; }) => {
                    element.acoes = [
                        new HeaderAction("mdi-pencil", "primary", "Teste", "editarItem", "200px"),
                        new HeaderAction("mdi-delete", "primary", "Teste", "deleteItem", "200px")
                    ]
                });

        this._view.loading(false)
                this._view.showItens(response.data)
            }).catch((e: any) => {
                this._view.loading(false)
                this._view.showMsg(e, 0)
            })
    }

    carregarTurnos() {
        //this._view.loading(true)


        axios
            .get("FuncionarioLocal")
            .then((response: any) => {
                this._view.carregaFuncionario(response.data)
                //this._view.loading(false)

            }).catch((e: any) => {
                this._view.loading(false)

            })
    }

    salvar(saidaAntecipada: any): void {

        this._view.loading(true);

        axios
          .post("SaidaAntecipada", saidaAntecipada)
          .then((response: any) => {
            this._view.loading(false);
            if (response.data.sucesso) {
              this._view.showMsg(response.data.message, 1);
              this.carregarItens();
            } else {
              this._view.showMsg("Não foi possível salvar os dados da solicitação de saída antecipada.", 0);
            }
          })
          .catch((e: any) => {
            this._view.loading(false);
            this._view.showMsg(e, 0);
          });

      }


