  dateFormatted = this.formatDate(new Date().toISOString().substr(0, 10));
  dateFormatted2 = this.formatDate(new Date().toISOString().substr(0, 10));
  dateFormatted3 = this.formatDate(new Date().toISOString().substr(0, 10));
