  //GENERO
  itemsGenero = ["MASCULINO", "FEMININO"];

  itemsDeficiencia = ["SIM", "NAO"];

  itemsStaff = ["STAFF", "DL", "IDL"];

  //ESTADO CIVIL
  itemsEstCivil = [
    "SOLTEIRO(A)",
    "CASADO(A)",
    "SEPARADO(A)",
    "DIVORCIADO(A)",
    "VIÚVO(A)",
    "UNIÃO ESTÁVEL",
  ];

  //SITUAÇÃO FUNCIONARIO
  itemsSitFuncionario = [];

  //CARGO FUNCIONARIO
  itemsCargoFuncionario = [];

  //CARGO FUNCIONARIO
  itemsSetorFuncionario = [];

  //CATEGORIA FUNCIONARIO
  itemsCategoriaFuncionario = [];

  //TURNO FUNCIONARIO
  itemsTurnoFuncionario = [];

  //ITENS PASTAS
  itemsPasta: any = [];

  pasta = {
    id: 0,
    nome: "",
    dataCadastro: new Date(),
    deletado: "N",
    producao: false,
    rh: false,
  };

  dataVencimentoMenu = false;
  dataNecessidadeMenu = false;
  dataNecessidadeItemMenu = false;

  isMobile = false;

  isSucesso = false;

  dialogLoading = false;
  dialogMessage = false;
  dialogAddPasta = false;
  dialogDeleteArquivo = false;
  textError = "";

  matricula = "";

  idFuncionario = "";

  funcionario = {
    cargo: "",
    cbo: "",
    cpf: "",
    ctps: "",
    deficiente: "",
    dtAdmissao: "",
    dtDemissao: new Date().toISOString().substr(0, 10),
    dtNascimento: new Date().toISOString().substr(0, 10),
    dtVencimentoExperiencia: undefined,
    estadoCivil: "",

    idFuncao: "",
    idSetor: "",
    idTipoMaoObra: "",
    idTurno: "",
    matricula: "",
    nome: "",
    pis: "",
    salario: 0,
    setor: "",
    sexo: "",
    situacao: "",
    staff: "",
    tipoMaoObra: "",
    turno: "",
    nomeMae: "",
    cep: "",
    endereco: "",
    bairro: "",
    municipio: "",
    estado: "",
    celular: "",
    deletado: "N",
    rg: "",
    tipo: "",
    funcionarioDocumento: [],
    frId: "",
  };

  //ITEMS DOCUMENTOS:
  documentos = {
    pasta: [],
    nome: "",
    nomeArquivo: "",
    type: "",
    anexoBase64: null,
    dataValidade: "",
    idFuncionario: 0,
    dataCadastro: new Date(),
  };
  itensDocumentos = this.documentos;
  itensDocumentosPastas: any = [];
  itemDelete: any = [];

  //SNACKBAR
  snackbar = false;
  text = "";
  vertical = true;
  color = "";
  colorBorder = "";

  uploadFile(event: any) {
    this.documentos.nomeArquivo = this.dFile.name;
    this.documentos.type = this.dFile.type.split("/")[1];
    fileUpload = this.dFile; //this.documentos.file;
    this.getBase64();
  }
  getBase64() {
    var reader = new FileReader();
    reader.readAsDataURL(fileUpload);
    reader.onload = function () {
      file = reader.result;
    };
    reader.onerror = function (error) {
      alert(error);
      // this.msgAviso = "Erro ao importar arquivo!";
      // this.dialogAviso = true;
      // return;
    };
  }

  validateAddDocumento() {
    if (this.documentos.nome == "" || this.documentos.nome.length > 30) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O campo Nome do Arquivo está vazio ou inválido.";
      this.snackbar = true;
      return true;
    }

    if (
      this.dFile.length == 0 ||
      this.dFile == null ||
      this.dFile == undefined
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione um arquivo válido para anexar ao documento.";
      this.snackbar = true;
      return true;
    }

    if (this.documentos.pasta.length == 0) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione a pasta para armazenar o arquivo.";
      this.snackbar = true;
      return true;
    }

    return false;
  }

  deleteDocumento() {
    this.itemsPasta.forEach((element: any) => {
      for (const { item, index } of element.children.map(
        (item: any, index: any) => ({
          item,
          index,
        })
      )) {
        if (item.id == this.itemDelete.id) {
          //console.log(item);
          element.children.splice(index, 1);
          //console.log(item.id);
          this._controller!.deleteDocumento(item.id);
          this.dialogDeleteArquivo = false;
          this.itemDelete = [];
          return;
        }
      }
    });
  }

  modalDeleteDocumento(itemDelete: any) {
    this.dialogDeleteArquivo = true;
    this.itemDelete = [];
    this.itemDelete = itemDelete;
  }

  modalCancelarDeleteDocumento() {
    this.dialogDeleteArquivo = false;
    this.itemDelete = [];
  }

  saveDocumento() {
    if (!this.validateAddDocumento()) {
      this.documentos.dataValidade = this.date3;
      this.documentos.anexoBase64 = file;

      this.itensDocumentos = this.documentos;

      this._controller!.salvarDocumento(
        this.itensDocumentos,
        this.idFuncionario
      );
    }
  }

  addPastas() {
    this.dialogAddPasta = true;
  }
  apenasNumeros(event: any) {
    validaNumeros(event);
  }

  validaCPF(cpf: any): boolean {
    let numeros, digitos, soma, i, resultado, digitosIguais;
    digitosIguais = 1;
    if (cpf.length < 11) return false;
    for (i = 0; i < cpf.length - 1; i++)
      if (cpf.charAt(i) != cpf.charAt(i + 1)) {
        digitosIguais = 0;
        break;
      }
    if (!digitosIguais) {
      numeros = cpf.substring(0, 9);
      digitos = cpf.substring(9);
      soma = 0;
      for (i = 10; i > 1; i--) soma += numeros.charAt(10 - i) * i;
      resultado = soma % 11 < 2 ? 0 : 11 - (soma % 11);
      if (resultado != digitos.charAt(0)) return false;
      numeros = cpf.substring(0, 10);
      soma = 0;
      for (i = 11; i > 1; i--) soma += numeros.charAt(11 - i) * i;
      resultado = soma % 11 < 2 ? 0 : 11 - (soma % 11);
      if (resultado != digitos.charAt(1)) return false;
      return true;
    } else return false;
  }

  formatDate(date: string) {
    if (!date) return null;

    const [year, month, day] = date.split("-");
    return `${day}/${month}/${year}`;
  }

  parseDate(date: string) {
    if (!date) return null;
    const [day, month, year] = date.split("/");
    return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
  }

  loading(visible: boolean): void {
    this.dialogLoading = visible;
  }

  fecharDialogMessage() {
    this.dialogMessage = false;
    if (this.isSucesso) {
      this.$router.push("/funcionarios");
    }
  }

  retornaListaFuncionarios() {
    this.$router.push("/funcionarios");
  }

  showMsg(msg: string, modo: number): void {
    this.textError = msg;
    if (modo == 1) {
      this.isSucesso = true;
    }
    this.dialogMessage = true;
  }

  validate() {
    // if (
    //   this.funcionario.matricula == "" ||
    //   this.funcionario.matricula.length > 6
    // ) {
    //   this.colorBorder = "error";
    //   this.color = "error";
    //   this.text = "O campo Matrícula está vazio ou inválido.";
    //   this.snackbar = true;
    //   return true;
    // }

    if (this.funcionario.nome == "" || this.funcionario.nome.length > 100) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O campo Nome está vazio ou inválido.";
      this.snackbar = true;
      return true;
    }

    if (this.funcionario.nome.toString().toUpperCase().indexOf("ADMINISTRADOR") != -1) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O Nome ADMINISTRADOR não pode ser utilizado. Funcionário já cadastrado no sistema.";
      this.snackbar = true;
      return true;
    }

    if (this.funcionario.cpf == "" || this.funcionario.cpf.length > 14) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O campo CPF deve ser preenchido.";
      this.snackbar = true;
      return true;
    } else if (
      this.funcionario.cpf != "" ||
      this.funcionario.cpf != null ||
      this.funcionario.cpf != undefined
    ) {
      let cpf = this.funcionario.cpf;
      cpf = cpf.replace(/[^\d]+/g, "");

      if (!this.validaCPF(cpf)) {
        this.colorBorder = "error";
        this.color = "error";
        this.text = "O CPF informador é inválido. Digite um CPF válido.";
        this.snackbar = true;
        return true;
      }
    }

    if (this.funcionario.rg == "" || this.funcionario.rg.length > 15) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O campo R.G. está vazio ou inválido.";
      this.snackbar = true;
      return true;
    }

    if (
      this.funcionario.nomeMae == "" ||
      this.funcionario.nomeMae.length > 100
    ) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O campo Nome da Mãe está vazio ou inválido.";
      this.snackbar = true;
      return true;
    }

    if (this.funcionario.ctps == "" || this.funcionario.ctps.length > 10) {
      this.colorBorder = "error";
      this.color = "error";
      this.text =
        "O campo Número da Carteira de Trabalho (CTPS) está vazio ou inválido.";
      this.snackbar = true;
      return true;
    }

    if (this.funcionario.pis == "" || this.funcionario.pis.length > 14) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O campo Número do PIS está vazio ou inválido.";
      this.snackbar = true;
      return true;
    }

    if (this.funcionario.cbo == "" || this.funcionario.cbo.length > 6) {
      this.colorBorder = "error";
      this.color = "error";
      this.text =
        "O campo Número da Carteira de Trabalho (CTPS) está vazio ou inválido.";
      this.snackbar = true;
      return true;
    }

    return false;
  }

  changeCargo(cargo: any) {
    this.funcionario.idFuncao = cargo.codigoGeneric;
    this.funcionario.cargo = cargo.campoGeneric;
  }

  changeSetor(setor: any) {
    this.funcionario.idSetor = setor.codigoGeneric;
    this.funcionario.setor = setor.campoGeneric;
  }

  changeCategoria(maoObra: any) {
    this.funcionario.idTipoMaoObra = maoObra.codigoGeneric;
    this.funcionario.tipoMaoObra = maoObra.campoGeneric;
  }

  changeTurno(turno: any) {
    this.funcionario.idTurno = turno.codigoGeneric;
    this.funcionario.turno = turno.campoGeneric;
  }

  saveFuncionarioLocal() {
    //console.log(file);
    if (this.validate()) {
    } else {
      let cpf = this.funcionario.cpf;
      cpf = cpf.replace(/[^\d]+/g, "");
      this.funcionario.cpf = cpf;

      let pis = this.funcionario.pis;
      pis = pis.replace(/[^\d]+/g, "");
      this.funcionario.pis = pis;

      this.funcionario.dtNascimento = this.date;
      this.funcionario.dtAdmissao = this.date2;

      //this.solicitacao.user = parseInt(Storage.get("User")!);

      this._controller!.salvar(this.funcionario);
      //this.retornaListaFuncionarios();
    }
  }

  validatePasta() {
    if (this.pasta.nome == "" || this.pasta.nome.length > 30) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "O campo Nome da Pasta está vazio ou inválido.";
      this.snackbar = true;
      return true;
    }

    if (this.pasta.rh == false && this.pasta.producao == false) {
      this.colorBorder = "error";
      this.color = "error";
      this.text = "Selecione o(s) Módulo(s) para a pasta.";
      this.snackbar = true;
      return true;
    }

    return false;
  }

  savePasta() {
    if (this.validatePasta()) {
    } else {
      //this.solicitacao.user = parseInt(Storage.get("User")!);

      this._controller!.salvarPasta(this.pasta, this.itensDocumentosPastas);
      this.pasta = {
        id: 0,
        nome: "",
        dataCadastro: new Date(),
        deletado: "N",
        producao: false,
        rh: false,
      };
      this.dialogAddPasta = false;

      //this.retornaListaFuncionarios();
    }
  }

  updateFuncionario() {
    if (this.validate()) {
    } else {
      let cpf = this.funcionario.cpf;
      cpf = cpf.replace(/[^\d]+/g, "");
      this.funcionario.cpf = cpf;

      let pis = this.funcionario.pis;
      pis = pis.replace(/[^\d]+/g, "");
      this.funcionario.pis = pis;

      this.funcionario.dtNascimento = this.date;
      this.funcionario.dtAdmissao = this.date2;

      //this.solicitacao.user = parseInt(Storage.get("User")!);
      this._controller!.update(this.idFuncionario, this.funcionario);
      //this.retornaListaFuncionarios();
    }
  }

  carregaDocumentos(item: any): void {
    this.itensDocumentosPastas = [];
    this.itensDocumentosPastas = JSON.parse(JSON.stringify(item));

    // console.log("CRIANDO NOVO ARQUIVO")
    // console.log(this.itensDocumentosPastas)

    this.documentos = {
      pasta: [],
      nome: "",
      nomeArquivo: "",
      type: "",
      anexoBase64: null,
      dataValidade: "",
      idFuncionario: 0,
      dataCadastro: new Date(),
    };

    this.dFile = [];

    let documentos = item;
    let aux = JSON.parse(JSON.stringify(this.itemsPasta));
    aux.forEach((element: any) => {
      element.children = [];
    });

    aux.forEach((element: any) => {
      documentos.forEach((item: any) => {
        if (element.id == item.idPasta) {
          element.children.push({
            id: item.documentoDTO.id,
            name: item.documentoDTO.nome,
            file: item.documentoDTO.anexo,
            nomeArquivo:
              item.documentoDTO.nomeArquivo + "." + item.documentoDTO.type,
            dataValidade: item.documentoDTO.dataValidade,
          });
        }
      });
    });

    this.itemsPasta = [];
    this.itemsPasta = aux;
  }

  carregaSequenciaMatriculaExterno(item: any) {
    this.funcionario.matricula = item.matricula;
  }

  carregaPasta(item: any) {
    this.itemsPasta = item;

    item.forEach((element: any) => {
      element.name = element.nome;
      element.children = [];
    });
  }

  carregaPastaDocumentos(itemDocs: any) {
    let documentos = itemDocs;
    let aux = JSON.parse(JSON.stringify(this.itemsPasta));
    aux.forEach((element: any) => {
      element.children = [];
    });

    aux.forEach((element: any) => {
      documentos.forEach((item: any) => {
        if (element.id == item.idPasta) {
          element.children.push({
            id: item.documentoDTO.id,
            name: item.documentoDTO.nome,
            file: item.documentoDTO.anexo,
            nomeArquivo:
              item.documentoDTO.nomeArquivo + "." + item.documentoDTO.type,
            dataValidade: item.documentoDTO.dataValidade,
          });
        }
      });
    });

    this.itemsPasta = [];
    this.itemsPasta = aux;
  }

  carregarDtos(): void {
    this.idFuncionario = this.$route.params.id;
    this.documentos.idFuncionario = parseInt(this.$route.params.id);
    this.matricula = this.$route.params.matricula;

    if (this.idFuncionario == undefined) {
      this.funcionario.tipo = "EXT";
      this.itemsTab = ["Dados Cadastrais"];
    } else {
      this.itemsTab = ["Dados Cadastrais", "Documentos"];
    }
  }

  showFuncionarioForEditar(item: any) {
    //CARREGANDO DADOS DO FUNCIONARIO
    this.funcionario = item;

    this.date = new Date(this.funcionario.dtNascimento)
      .toISOString()
      .substr(0, 10);

    this.date2 = new Date(this.funcionario.dtAdmissao)
      .toISOString()
      .substr(0, 10);

    this.funcionario.funcionarioDocumento = item.funcionarioDocumento;

    this.itensDocumentosPastas = [];
    this.itensDocumentosPastas = JSON.parse(
      JSON.stringify(item.funcionarioDocumento)
    );
    // console.log("CARREGA EDITAR ");
    // console.log(this.itensDocumentosPastas)

    this.itemsPasta.forEach((element: any) => {
      for (const { item, index } of this.funcionario.funcionarioDocumento.map(
        (item: any, index: any) => ({
          item,
          index,
        })
      )) {
        if (element.id == item.idPasta) {
          element.children.push({
            id: item.documentoDTO.id,
            name: item.documentoDTO.nome,
            file: item.documentoDTO.anexo,
            nomeArquivo:
              item.documentoDTO.nomeArquivo + "." + item.documentoDTO.type,
            dataValidade: item.documentoDTO.dataValidade,
          });
        }
      }
    });
  }

  carregaSituacaoFuncionario(item: any) {
    this.itemsSitFuncionario = item;
  }

  carregaCargoFuncionario(item: any) {
    this.itemsCargoFuncionario = item;
  }

  carregaSetorFuncionario(item: any) {
    this.itemsSetorFuncionario = item;
  }

  carregaCategoriaFuncionario(item: any) {
    this.itemsCategoriaFuncionario = item;
  }

  carregaTurnoFuncionario(item: any) {
    this.itemsTurnoFuncionario = item;
  }

  created() {
    this._controller = new FuncionarioFormController(this);
    this.idFuncionario = this.$route.params.id;

    this.carregarDtos();

    this._controller.carregarDtos(this.$route.params.matricula);
    this.isMobile = Display.isMobile();
  }

  @Watch("date")
  onDatasChanged(val: string) {
    this.dateFormatted = this.formatDate(this.date);
  }

  @Watch("date2")
  onDatas2Changed(val: string) {
    this.dateFormatted2 = this.formatDate(this.date2);
  }
}
